import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css'; // This is where you will import your CSS file

function Navbar() {
  const tabContainerRef = useRef(null);
  const sliderRef = useRef(null);

  useEffect(() => {
    const onScroll = () => {
      checkTabContainerPosition();
      findCurrentTabSelector();
    };
    
    const onResize = () => {
      setSliderCss();
    };

    const onTabClick = (event, element) => {
      event.preventDefault();
      const target = document.querySelector(element.getAttribute('href'));
      const scrollTop = target.offsetTop - tabContainerRef.current.offsetHeight + 1;
      window.scrollTo({ top: scrollTop, behavior: 'smooth' });
    };

    const checkTabContainerPosition = () => {
      const offset = tabContainerRef.current.offsetTop + tabContainerRef.current.offsetHeight - 70;
      if (window.scrollY > offset) {
        tabContainerRef.current.classList.add('et-hero-tabs-container--top');
      } else {
        tabContainerRef.current.classList.remove('et-hero-tabs-container--top');
      }
    };

    const findCurrentTabSelector = () => {
      let newCurrentId;
      let newCurrentTab;
      document.querySelectorAll('.et-hero-tab').forEach(tab => {
        const id = tab.getAttribute('href');
        const target = document.querySelector(id);
        const offsetTop = target.offsetTop - tabContainerRef.current.offsetHeight;
        const offsetBottom = target.offsetTop + target.offsetHeight - tabContainerRef.current.offsetHeight;
        if (window.scrollY > offsetTop && window.scrollY < offsetBottom) {
          newCurrentId = id;
          newCurrentTab = tab;
        }
      });
      if (newCurrentId !== null) {
        setSliderCss(newCurrentTab);
      }
    };

    const setSliderCss = (tab) => {
      if (tab && sliderRef.current) {
        sliderRef.current.style.width = tab.offsetWidth + 'px';
        sliderRef.current.style.left = tab.offsetLeft + 'px';
      }
    };

    document.querySelectorAll('.et-hero-tab').forEach(tab => {
      tab.addEventListener('click', (event) => onTabClick(event, tab));
    });
    window.addEventListener('scroll', onScroll);
    window.addEventListener('resize', onResize);

    return () => {
      window.removeEventListener('scroll', onScroll);
      window.removeEventListener('resize', onResize);
    };
  }, []);

  return (
    <nav className="et-hero-tabs">
      <div className="et-hero-tabs-container" ref={tabContainerRef}>
        <Link className="et-hero-tab" to="#tab-es6">ES6</Link>
        <Link className="et-hero-tab" to="#tab-flexbox">Flexbox</Link>
        <Link className="et-hero-tab" to="#tab-react">React</Link>
        <Link className="et-hero-tab" to="#tab-angular">Angular</Link>
        <Link className="et-hero-tab" to="#tab-other">Other</Link>
        <span className="et-hero-tab-slider" ref={sliderRef}></span>
      </div>
    </nav>
  );
}

export default Navbar;
