import React from 'react';
import { motion } from 'framer-motion'; // Add Framer Motion for scroll animations

const AboutPage = () => {
  return (
    <section className="py-20 bg-custom-pearl-gray">
      <div className="max-w-screen-xl mx-auto px-6 lg:px-8 text-left">
        
        {/* Key Features Section */}
        <motion.div
          className="mb-16 bg-custom-cloud p-10 rounded-lg shadow-md"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
        >
          <h2 className="text-5xl font-extrabold text-custom-blue mb-8 text-center transition-opacity duration-300 hover:opacity-80">
            Our Key Features
          </h2>
          <p className="text-xl text-gray-700 mb-10 max-w-2xl mx-auto text-center">
            At DStar, we focus on creating a nurturing and secure environment for your child. 
            We implement features aimed at their comfort and well-being:
          </p>
          <ul className="grid grid-cols-1 md:grid-cols-2 gap-6 text-lg text-gray-700 space-y-4 md:space-y-0">
            <li className="transition-transform transform hover:scale-105">
              üöç Pickup services from local Primary Schools and school bus drop-off points
            </li>
            <li className="transition-transform transform hover:scale-105">
              üßº A clean, safe environment with constant oversight
            </li>
            {/* Other features... */}
          </ul>
        </motion.div>

        {/* Philosophy Section */}
        <motion.div
          className="mb-16 bg-custom-primrose p-10 rounded-lg shadow-md"
          initial={{ opacity: 0, x: -50 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <h2 className="text-5xl font-extrabold text-custom-misty-red mb-6 text-center transition-colors duration-300 hover:text-custom-orange">
            Our Philosophy
          </h2>
          <p className="text-xl text-gray-800 max-w-3xl mx-auto text-center">
            At DStar, we emphasize character development by fostering a love for learning. 
            We believe in the importance of learning, playing, and growing together to build a solid foundation for the future.
          </p>
        </motion.div>

        {/* Company Profile Section */}
        <motion.div
          className="mb-16 bg-custom-beige p-10 rounded-lg shadow-md"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
        >
          <h2 className="text-5xl font-extrabold text-custom-palmhouse mb-6 text-center transition-opacity duration-300 hover:opacity-80">
            About Our Company
          </h2>
          <p className="text-xl text-gray-700 max-w-3xl mx-auto text-center">
            DStar Pte Ltd operates under the Smart Edu Group, a leader in early childhood education and executive training. 
            With a team of highly qualified professionals, we offer innovative learning programs designed to enhance every child‚Äôs educational experience.
          </p>
        </motion.div>

        {/* Mission Section */}
        <motion.div
          className="mb-16 bg-custom-cloud p-10 rounded-lg shadow-md"
          initial={{ opacity: 0, x: 50 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <h2 className="text-5xl font-extrabold text-custom-misty-red mb-6 text-center transition-transform duration-300 hover:scale-105">
            Our Commitment
          </h2>
          <p className="text-xl text-gray-700 max-w-3xl mx-auto text-center">
            We foster character growth and a love for education by providing an enriching learning environment, 
            offering children the building blocks they need for lifelong success.
          </p>
        </motion.div>

        {/* Vision Section */}
        <motion.div
          className="mb-16 bg-custom-pearl-gray p-10 rounded-lg shadow-md"
          initial={{ opacity: 0, x: -50 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <h2 className="text-5xl font-extrabold text-custom-blue mb-6 text-center transition-transform duration-300 hover:scale-105">
            Our Aspiration
          </h2>
          <p className="text-xl text-gray-700 max-w-3xl mx-auto text-center">
            We aim to be recognized internationally for our excellence in education, 
            creating a positive and safe learning environment that supports lifelong learning.
          </p>
        </motion.div>
      </div>
    </section>
  );
};

export default AboutPage;
