import React, { useEffect, useRef, useState } from 'react';

function DevelopingFutureStars() {
  const [isVisible, setIsVisible] = useState(false);
  const imageRef = useRef();

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        setIsVisible(entry.isIntersecting);
      },
      {
        threshold: 0.1, // The image becomes visible when 10% is in view
      }
    );

    if (imageRef.current) {
      observer.observe(imageRef.current);
    }

    return () => {
      if (imageRef.current) {
        observer.unobserve(imageRef.current);
      }
    };
  }, []);

  return (
    <section className="py-16 bg-white">
      <div className="max-w-screen-xl mx-auto px-4 flex flex-col md:flex-row items-center">
        
        {/* Image Section */}
        <div className="w-full md:w-1/2 flex justify-center mb-8 md:mb-0">
          {/* Image without container cropping */}
          <img
            ref={imageRef}
            src={`${process.env.PUBLIC_URL}/Photos/Dstar_logo.png`} // Adjust the image path as needed
            alt="Developing Future Stars"
            className={`transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}
            style={{ transition: 'opacity 1s ease-in-out' }} // Smooth transition effect
          />
        </div>

        {/* Text Section */}
        <div className="w-full md:w-1/2 ml-8">
          <h2 className="text-3xl font-bold text-black mb-4">Developing Future Stars</h2>
          <p className="text-lg text-gray-700 mb-4">
            Upon entering pre-school, usually children will bring with them a variety of personal and social skills, values, and attitudes. No matter how little these are, it is important that they foster these values and from the relationships and experiences within the home and immediate environment, we will help them to cultivate their learning environment.
          </p>
          <p className="text-lg text-gray-700">
            We provide classes for:
          </p>
          {/* Additional content like bullet points or classes list can be added here */}
        </div>
      </div>
    </section>
  );
}

export default DevelopingFutureStars;
