import React from 'react';

// Helper function to import all images from src/assets/photos folder dynamically
function importAll(r) {
  let images = {};
  r.keys().forEach((item, index) => {
    images[item.replace('./', '')] = r(item);
  });
  return images;
}

// Import all images from the src/assets/photos folder
const images = importAll(require.context('../assets/photos', false, /\.(png|jpe?g|svg)$/));

function GalleryPage() {
  // Get image entries as an array and sort them by key (filename)
  const imageEntries = Object.entries(images).sort((a, b) => b[0].localeCompare(a[0])); // Newest first based on filename

  return (
    <section
      className="relative bg-cover bg-center py-16"
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL}/Background/Yellow.png)`,
        backgroundSize: '100%' // Adjust the percentage as needed
      }}
    >
      {/* Gallery Container */}
      <div className="bg-custom-white p-8 rounded-lg shadow-lg max-w-screen-lg mx-auto">
        {/* Gallery Header */}
        <header className="text-center py-8">
          <h1 className="text-4xl font-bold">Gallery</h1>
          <p className="text-gray-500 mt-4">Explore our collection of beautiful images captured during various events and moments.</p>
        </header>

        {/* Gallery Grid */}
        <div className="px-4">
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            {imageEntries.map(([imageName, imageSrc], index) => (
              <div key={index} className="relative group">
                {/* Image Card */}
                <img
                  src={imageSrc}
                  alt={imageName.replace(/\.[^/.]+$/, '')} // Remove file extension for alt text
                  className="w-full h-64 object-cover rounded-lg shadow-lg transition-transform transform hover:scale-105 duration-300"
                />
              </div>
            ))}
          </div>
        </div>

        {/* Pagination or Navigation (optional) */}
        <div className="text-center py-8">
          <button className="px-4 py-2 bg-custom-blue text-white rounded-lg shadow-lg hover:bg-custom-misty-red transition duration-300">
            Load More
          </button>
        </div>

        {/* Contribute Card */}
        <div className="mt-8 p-6 bg-custom-goose-gray text-center rounded-lg shadow-lg transition-transform transform hover:scale-105 hover:animate-shake focus:animate-shake cursor-pointer duration-300">
          <h2 className="text-2xl font-bold mb-4">Want to contribute your photos?</h2>
          <p className="text-lg text-gray-700 mb-6">
            Have photos you'd like to share? Email us your best shots and you might see them featured in our gallery!
          </p>
          <p className="text-lg text-custom-blue font-semibold">Email us at: dstar.jw@gmail.com</p>
        </div>
      </div>
    </section>
  );
}

export default GalleryPage;
