import React, { useState, useEffect } from 'react';

function HeroSection() {
  const images = [
    `${process.env.PUBLIC_URL}/Photos/IMG-20240719-WA0045.jpg`,
    `${process.env.PUBLIC_URL}/Photos/IMG-20240719-WA0042.jpg`,
    `${process.env.PUBLIC_URL}/Photos/IMG-20240719-WA0021`,
  ];

  const [currentImage, setCurrentImage] = useState(0);

  // Cycle through the images every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage + 1) % images.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <section
      className="bg-cover bg-center h-screen flex items-center justify-center text-white relative"
      style={{
        backgroundImage: `url(${images[currentImage]})`,
        backgroundPosition: 'center 40%',
        transition: 'background-image 1s ease-in-out',
      }}
    >
      <div className="text-center">
        <h1 className="text-5xl font-bold">Welcome to Our Daycare</h1>
        <p className="mt-4 text-xl">Where learning and fun go hand in hand</p>
        <a
          href="#registration"
          className="mt-6 inline-block bg-custom-blue text-white py-2 px-4 rounded hover:bg-blue-700 transition"
        >
          Join Us Today
        </a>
      </div>

      {/* Navigation Dots */}
      <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 flex space-x-2">
        {images.map((_, index) => (
          <button
            key={index}
            className={`w-4 h-4 rounded-full ${
              index === currentImage ? 'bg-red-500' : 'bg-gray-300'
            }`}
            onClick={() => setCurrentImage(index)}
          />
        ))}
      </div>
    </section>
  );
}

export default HeroSection;
