import React, { useState } from 'react';

const Registration = () => {
  const [formData, setFormData] = useState({
    childName: '',
    childAge: '',
    childSchool: '',
    childGrade: 'Kindergarten',
    parentName: '',
    parentContact: '',
    parentEmail: '',
    additionalInfo: ''
  });

  const [formSubmitted, setFormSubmitted] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Example of form submission via email (requires backend or email service like EmailJS)
    // sendEmail(formData) or handle via backend API

    setFormSubmitted(true); // Show the success message
  };

  const closePopup = () => {
    setFormSubmitted(false);
  };

  return (
    <section id="registration" className="py-16 bg-custom-primrose text-white relative">
      <div className="max-w-screen-xl mx-auto px-4 text-center">
        <h2 className="text-4xl font-bold">Register Your Child</h2>
        <p className="mt-4 text-lg">
          Ready to join our daycare family? Weâ€™re excited to welcome your child to our community. Register today to secure your spot!
        </p>

        {/* Registration Form */}
        {!formSubmitted && (
          <form onSubmit={handleSubmit} className="mt-8 text-left max-w-lg mx-auto">
            <div className="mb-4">
              <label className="block text-lg mb-2" htmlFor="childName">Child's Name</label>
              <input
                type="text"
                name="childName"
                id="childName"
                value={formData.childName}
                onChange={handleInputChange}
                className="w-full p-2 rounded text-black"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-lg mb-2" htmlFor="childAge">Child's Age</label>
              <input
                type="number"
                name="childAge"
                id="childAge"
                value={formData.childAge}
                onChange={handleInputChange}
                className="w-full p-2 rounded text-black"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-lg mb-2" htmlFor="childSchool">Child's School</label>
              <input
                type="text"
                name="childSchool"
                id="childSchool"
                value={formData.childSchool}
                onChange={handleInputChange}
                className="w-full p-2 rounded text-black"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-lg mb-2" htmlFor="childGrade">Grade</label>
              <select
                name="childGrade"
                id="childGrade"
                value={formData.childGrade}
                onChange={handleInputChange}
                className="w-full p-2 rounded text-black"
                required
              >
                <option value="Kindergarten">Kindergarten</option>
                <option value="Primary 1">Primary 1</option>
                <option value="Primary 2">Primary 2</option>
                <option value="Primary 3">Primary 3</option>
                <option value="Primary 4">Primary 4</option>
                <option value="Primary 5">Primary 5</option>
                <option value="Primary 6">Primary 6</option>
                <option value="Secondary 1">Secondary 1</option>
                <option value="Secondary 2">Secondary 2</option>
                <option value="Secondary 3">Secondary 3</option>
                <option value="Secondary 4">Secondary 4</option>
              </select>
            </div>

            <div className="mb-4">
              <label className="block text-lg mb-2" htmlFor="parentName">Parent's Name</label>
              <input
                type="text"
                name="parentName"
                id="parentName"
                value={formData.parentName}
                onChange={handleInputChange}
                className="w-full p-2 rounded text-black"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-lg mb-2" htmlFor="parentContact">Parent's Contact Number</label>
              <input
                type="tel"
                name="parentContact"
                id="parentContact"
                value={formData.parentContact}
                onChange={handleInputChange}
                className="w-full p-2 rounded text-black"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-lg mb-2" htmlFor="parentEmail">Parent's Email</label>
              <input
                type="email"
                name="parentEmail"
                id="parentEmail"
                value={formData.parentEmail}
                onChange={handleInputChange}
                className="w-full p-2 rounded text-black"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-lg mb-2" htmlFor="additionalInfo">Additional Information</label>
              <textarea
                name="additionalInfo"
                id="additionalInfo"
                value={formData.additionalInfo}
                onChange={handleInputChange}
                className="w-full p-2 rounded text-black"
                rows="4"
              />
            </div>

            <button type="submit" className="mt-6 bg-white text-custom-blue py-2 px-4 rounded hover:bg-gray-200 transition">Submit Registration</button>
          </form>
        )}

        {/* Success Message */}
        {formSubmitted && (
          <div className="fixed top-0 right-0 w-96 h-full bg-white shadow-lg p-8 flex flex-col justify-center items-center">
            <h3 className="text-2xl font-bold text-custom-blue">Form Submitted</h3>
            <p className="mt-4 text-lg text-gray-700">A representative will contact you in 1-2 business days.</p>
            <button onClick={closePopup} className="mt-6 bg-custom-blue text-white py-2 px-4 rounded hover:bg-blue-700 transition">Close</button>
          </div>
        )}
      </div>
    </section>
  );
}

export default Registration;
