import React, { useState, useEffect } from 'react';

function HeroSection() {
  const images = [
    `${process.env.PUBLIC_URL}/Photos/IMG-20240719-WA0045.jpg`,
    `${process.env.PUBLIC_URL}/Photos/IMG-20240719-WA0042.jpg`,
    `${process.env.PUBLIC_URL}/Photos/IMG-20240521-WA0021.jpg`,
  ];

  const texts = [
    {
      title: "Welcome to Dstar Daycare",
      subtitle: "Enrollment for 2025/26 now available",
    },
    {
      title: "Join Our Friendly Community",
      subtitle: "Creating a nurturing environment for your child",
    },
    {
      title: "Enroll Your Child Today",
      subtitle: "Building a foundation for a brighter future",
    },
  ];

  const [currentImage, setCurrentImage] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      handleImageChange((currentImage + 1) % images.length);
    }, 10000);
    return () => clearInterval(interval);
  }, [currentImage]);

  const handleImageChange = (index) => {
    setIsTransitioning(true);
    setTimeout(() => {
      setCurrentImage(index);
      setIsTransitioning(false);
    }, 1000); // Adjusted for smoother transition timing
  };

  return (
    <section className="relative h-screen flex items-center justify-start text-white overflow-hidden">
      {/* Background Images */}
      {images.map((image, index) => (
        <div
          key={index}
          className={`absolute top-0 left-0 w-full h-full bg-cover bg-center transition-opacity duration-1000 ease-in-out ${
            currentImage === index
              ? "opacity-100 z-10"
              : "opacity-0 z-0"
          }`}
          style={{
            backgroundImage: `url(${image})`,
            backgroundPosition: 'center 40%',
          }}
        />
      ))}

      <div className="relative text-left w-full max-w-screen-xl px-4">
        {/* Text Content */}
        <div
          className={`transition-opacity duration-1000 ${
            isTransitioning ? "opacity-0" : "opacity-100"
          } p-20 shadow-lg max-w-xl bg-custom-blue`}
          style={{ clipPath: "url(#blob)", marginLeft: '2rem' }}
        >
          <h1 className="text-4xl font-bold">{texts[currentImage].title}</h1>
          <p className="mt-4 text-lg">{texts[currentImage].subtitle}</p>
          <a
            href="registration"
            className="mt-6 inline-block bg-red-500 text-white py-3 px-6 rounded-full hover:bg-red-600 transition"
          >
            Join Us Today
          </a>
        </div>
      </div>

      {/* Navigation Dots */}
      <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 flex space-x-2">
        {images.map((_, index) => (
          <button
            key={index}
            className={`w-4 h-4 rounded-full ${
              index === currentImage ? 'bg-red-500' : 'bg-gray-300'
            }`}
            onClick={() => handleImageChange(index)}
          />
        ))}
      </div>

      {/* Blob Shape Definition */}
      <svg width="0" height="0">
        <defs>
          <clipPath id="blob" clipPathUnits="objectBoundingBox">
            <path
              d="M0.2,0.1 C0.45,-0.1,0.75,0.2,0.85,0.35 C0.95,0.5,0.85,0.85,0.6,0.95 C0.35,1,0.15,0.85,0.05,0.6 C-0.05,0.35,0.1,0.15,0.2,0.1"
            />
          </clipPath>
        </defs>
      </svg>
    </section>
  );
}

export default HeroSection;
