import React, { useEffect, useRef } from 'react';

const NewsPage = () => {
  const iframeRef = useRef(null);

  useEffect(() => {
    const adjustIframeHeight = () => {
      const iframe = iframeRef.current;
      if (iframe) {
        iframe.style.height = iframe.contentWindow.document.body.scrollHeight + 'px';
      }
    };

    const iframe = iframeRef.current;
    if (iframe) {
      iframe.addEventListener('load', adjustIframeHeight);
      window.addEventListener('resize', adjustIframeHeight); // Adjust height on window resize
    }

    return () => {
      if (iframe) {
        iframe.removeEventListener('load', adjustIframeHeight);
        window.removeEventListener('resize', adjustIframeHeight);
      }
    };
  }, []);

  return (
    <div className="min-h-screen bg-custom-pearl-gray flex flex-col items-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-screen-lg w-full bg-white shadow-lg rounded-lg p-8">
        <h2 className="text-4xl font-bold text-center mb-8 text-custom-blue">Latest News</h2>
        <p className="text-lg text-gray-700 mb-8 text-center">
          Stay up to date with the latest updates from DStar EduCentre. Follow us on Instagram to see our latest events, activities, and announcements.
        </p>

        {/* Instagram Widget */}
        <div className="w-full">
          <iframe
            ref={iframeRef}
            src="https://cdn.lightwidget.com/widgets/0e1d6716a54650699cf8a6b6809e2747.html"
            scrolling="no"
            allowTransparency="true"
            className="lightwidget-widget"
            style={{ width: '100%', border: 0, overflow: 'hidden' }} 
            onLoad={() => {
              const iframe = iframeRef.current;
              iframe.style.height = iframe.contentWindow.document.body.scrollHeight + 'px';
            }}
          ></iframe>
        </div>
      </div>
    </div>
  );
};

export default NewsPage;
