import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

function Navbar() {
  const location = useLocation();
  const [activeItem, setActiveItem] = useState(null);

  useEffect(() => {
    const activeElement = document.querySelector('.navbar-nav .active');
    if (activeElement) {
      moveSelector(activeElement);
    }
  }, [location]);

  const moveSelector = (element) => {
    const horiSelector = document.querySelector('.hori-selector');
    const { offsetTop, offsetLeft, offsetHeight, offsetWidth } = element;
    horiSelector.style.top = `${offsetTop}px`;
    horiSelector.style.left = `${offsetLeft}px`;
    horiSelector.style.height = `${offsetHeight}px`;
    horiSelector.style.width = `${offsetWidth}px`;
  };

  const handleNavItemClick = (e, index) => {
    setActiveItem(index);
    const navItems = document.querySelectorAll('.navbar-nav li');
    navItems.forEach((item, idx) => {
      item.classList.toggle('active', idx === index);
    });
  };

  return (
    <nav className="bg-blue-600 p-4 relative">
      <Link className="text-white text-lg font-bold" to="#">Navbar</Link>
      <button className="navbar-toggler text-white" aria-label="Toggle navigation">
        <i className="fas fa-bars"></i>
      </button>
      <div className="navbar-collapse">
        <ul className="navbar-nav flex space-x-4">
          <div className="hori-selector absolute bg-white rounded-t-lg transition-all duration-600 ease-out"></div>
          {['/', '/about-us', '/gallery', '/registration', '/fees', '/news'].map((path, index) => (
            <li
              key={path}
              className={`nav-item ${location.pathname === path ? 'active' : ''}`}
              onClick={(e) => handleNavItemClick(e, index)}
            >
              <Link to={path} className="nav-link text-white hover:text-blue-300 transition duration-500 ease-in-out flex items-center">
                <i className="fas fa-icon"></i>{path.replace('/', '').replace('-', ' ') || 'Home'}
              </Link>
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
