import React, { useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css'; // Importing the CSS file

function Navbar() {
  const location = useLocation();

  useEffect(() => {
    const activeElement = document.querySelector('.navbar-nav .active');
    if (activeElement) {
      moveSelector(activeElement);
    }
  }, [location]);

  const moveSelector = (element) => {
    const horiSelector = document.querySelector('.hori-selector');
    const { offsetTop, offsetLeft, offsetHeight, offsetWidth } = element;
    horiSelector.style.top = `${offsetTop}px`;
    horiSelector.style.left = `${offsetLeft}px`;
    horiSelector.style.height = `${offsetHeight}px`;
    horiSelector.style.width = `${offsetWidth}px`;
  };

  const handleNavItemClick = (e, index) => {
    const navItems = document.querySelectorAll('.navbar-nav li');
    navItems.forEach((item, idx) => {
      item.classList.toggle('active', idx === index);
    });
  };

  return (
    <nav className="navbar navbar-expand-custom navbar-mainbg">
      <Link className="navbar-brand navbar-logo" to="#">Navbar</Link>
      <button className="navbar-toggler" aria-label="Toggle navigation">
        <i className="fas fa-bars text-white"></i>
      </button>
      <div className="collapse navbar-collapse" id="navbarSupportedContent">
        <ul className="navbar-nav">
          <div className="hori-selector"><div className="left"></div><div className="right"></div></div>
          {[
            { path: '/', label: 'Home' },
            { path: '/about-us', label: 'About Us' },
            { path: '/gallery', label: 'Gallery' },
            { path: '/registration', label: 'Registration' },
            { path: '/contact-us', label: 'Contact Us' }
          ].map((link, index) => (
            <li
              key={link.path}
              className={`nav-item ${location.pathname === link.path ? 'active' : ''}`}
              onClick={(e) => handleNavItemClick(e, index)}
            >
              <Link to={link.path} className="nav-link">
                {link.label}
              </Link>
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
