import React, { useEffect, useRef, useState } from 'react';

function DevelopingFutureStars() {
  const [isVisible, setIsVisible] = useState(false);
  const imageRef = useRef();

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        setIsVisible(entry.isIntersecting);
      },
      {
        threshold: 0.2, // The image becomes visible when 10% is in view
      }
    );

    if (imageRef.current) {
      observer.observe(imageRef.current);
    }

    return () => {
      if (imageRef.current) {
        observer.unobserve(imageRef.current);
      }
    };
  }, []);

  return (
    <section className="relative py-16 bg-white">
      {/* Cloud Shape SVG */}
      <div className="absolute top-0 left-0 w-full">
        <svg
          viewBox="0 0 1440 320"
          xmlns="http://www.w3.org/2000/svg"
          className="w-full h-full"
        >
          <path
            fill="#A1E6E2" // Cloud color, you can change it
            d="M0,224L40,218.7C80,213,160,203,240,192C320,181,400,171,480,160C560,149,640,139,720,154.7C800,171,880,213,960,218.7C1040,224,1120,192,1200,176C1280,160,1360,160,1400,160L1440,160L1440,0L1400,0C1360,0,1280,0,1200,0C1120,0,1040,0,960,0C880,0,800,0,720,0C640,0,560,0,480,0C400,0,320,0,240,0C160,0,80,0,40,0L0,0Z"
          />
        </svg>
      </div>

      <div className="max-w-screen-xl mx-auto px-4 flex flex-col md:flex-row items-center relative z-10">
        {/* Image Section */}
        <div className="w-full md:w-1/2 flex justify-center mb-8 md:mb-0">
          {/* Image without container cropping */}
          <img
            ref={imageRef}
            src={`${process.env.PUBLIC_URL}/Photos/Dstar_logo.png`} // Adjust the image path as needed
            alt="Developing Future Stars"
            className={`transition-opacity duration-2000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}
            style={{ transition: 'opacity 2s ease-in-out' }} // Smooth transition effect
          />
        </div>

        {/* Text Section */}
        <div className="w-full md:w-1/2 ml-8">
          <h2 className="text-3xl font-bold text-black mb-4">Developing Future Stars</h2>
          <p className="text-lg text-gray-700 mb-4">
            Upon entering pre-school, usually children will bring with them a variety of personal and social skills, values, and attitudes. No matter how little these are, it is important that they foster these values and from the relationships and experiences within the home and immediate environment, we will help them to cultivate their learning environment.
          </p>
          <p className="text-lg text-gray-700">
            We provide classes for:
          </p>
        </div>
      </div>
    </section>
  );
}

export default DevelopingFutureStars;
