import React, { useState, useEffect } from 'react';

function HeroSection() {
  const images = [
    `${process.env.PUBLIC_URL}/Photos/IMG-20240719-WA0045.jpg`,
    `${process.env.PUBLIC_URL}/Photos/IMG-20240719-WA0042.jpg`,
    `${process.env.PUBLIC_URL}/Photos/IMG-20240521-WA0021.jpg`,
  ];

  const texts = [
    {
      title: "Welcome to Our Daycare",
      subtitle: "Where learning and fun go hand in hand",
    },
    {
      title: "Join Our Friendly Community",
      subtitle: "Creating a nurturing environment for your child",
    },
    {
      title: "Enroll Your Child Today",
      subtitle: "Building a foundation for a brighter future",
    },
  ];

  const [currentImage, setCurrentImage] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);

  // Cycle through the images and texts every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setIsTransitioning(true);
      setTimeout(() => {
        setCurrentImage((prevImage) => (prevImage + 1) % images.length);
        setIsTransitioning(false);
      }, 500); // Time for swipe-out effect before switching
    }, 5000);
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <section
      className="bg-cover bg-center h-screen flex items-center justify-center text-white relative"
      style={{
        backgroundImage: `url(${images[currentImage]})`,
        backgroundPosition: 'center 40%',
        transition: 'background-image 1s ease-in-out',
      }}
    >
      <div className="text-center relative">
        {/* Animated Text Container */}
        <div
          className={`transform transition-all duration-500 ${
            isTransitioning ? "opacity-0 -translate-x-full" : "opacity-100 translate-x-0"
          } rounded-lg bg-custom-blue p-8 shadow-lg max-w-lg mx-auto`}
          style={{ clipPath: "polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%)" }} // Unique shape using clip-path
        >
          <h1 className="text-4xl font-bold">{texts[currentImage].title}</h1>
          <p className="mt-4 text-lg">{texts[currentImage].subtitle}</p>
          <a
            href="#registration"
            className="mt-6 inline-block bg-red-500 text-white py-2 px-4 rounded-full hover:bg-red-600 transition"
          >
            Join Us Today
          </a>
        </div>
      </div>

      {/* Navigation Dots */}
      <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 flex space-x-2">
        {images.map((_, index) => (
          <button
            key={index}
            className={`w-4 h-4 rounded-full ${
              index === currentImage ? 'bg-red-500' : 'bg-gray-300'
            }`}
            onClick={() => setCurrentImage(index)}
          />
        ))}
      </div>
    </section>
  );
}

export default HeroSection;
