import React, { useState, useEffect } from 'react';

function Intro() {
  const images = [
    `${process.env.PUBLIC_URL}/Photos/20240701_141845(0).jpg`,
    `${process.env.PUBLIC_URL}/Photos/IMG-20240719-WA0021.jpg`,
    `${process.env.PUBLIC_URL}/Photos/IMG-20240719-WA0042.jpg`, // Add a third image path here
  ];

  const [currentImage, setCurrentImage] = useState(0);

  // Cycle through the images every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage + 1) % images.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <section className="py-16 bg-custom-pearl-gray relative">
      <div className="max-w-screen-xl mx-auto px-4 flex flex-col md:flex-row items-center">
        {/* Image Section */}
        <div className="w-full md:w-1/2 flex justify-center mb-8 md:mb-0 relative">
          <div className="rounded-full overflow-hidden w-80 h-80 flex-shrink-0 relative animate-fluid-wave">
            {/* Animated Circle Border */}
            <div className="absolute inset-0 w-full h-full rounded-full border-4 border-blue-300 animate-rotate-slow" />
            <img
              src={images[currentImage]}
              alt="Main Objective"
              className="w-full h-full object-cover transform scale-125"
            />
          </div>

          {/* Navigation Dots */}
          <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 flex space-x-2 mt-4">
            {images.map((_, index) => (
              <button
                key={index}
                className={`w-4 h-4 rounded-full ${index === currentImage ? 'bg-red-500' : 'bg-gray-300'}`}
                onClick={() => setCurrentImage(index)}
              />
            ))}
          </div>
        </div>

        {/* Text Section */}
        <div className="w-full md:w-1/2 text-center md:text-left">
          <h2 className="text-red-500 text-sm uppercase mb-2">Welcome to [Daycare Name]</h2>
          <h2 className="text-4xl font-bold mb-4">Our Main Objective</h2>
          <p className="text-lg text-gray-700 mb-4">
            [Daycare Name], founded by leaders in early childhood education, is dedicated to fostering good character and a love for learning in children. Using our tailored teaching methods, we promote independent thought and provide a curriculum tailored to each child’s needs.
          </p>
          <p className="text-lg text-gray-700 italic">
            "The first five years have so much to do with how the next 80 turn out." — Bill Gates
          </p>

          {/* Join Now Button */}
          <div className="mt-6">
            <a href="#registration" className="bg-red-500 text-white py-3 px-6 rounded-full shadow-lg hover:bg-red-600 transition">
              Join Now
            </a>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Intro;
